      font-size: 3rem;
      font-weight: 300;
      padding: 1rem 2rem;
      color: $color-white;
      text-decoration: none;
      text-transform: uppercase;
      background-image: linear-gradient(120deg, transparent 50%, $color-white 50%);
      background-size: 250%;
      transition: all .4s;

      span {
        margin-right: 1.5rem;
        display: inline-block;
      }
    }

    &:hover,
    &:active {
      background-position: 100%;
      color: $color-primary;
      transform: translateX(1rem); //if not inline-block this won't work
    }
  }

  &__checkbox:checked ~ &__background {
    transform: scale(80);
  }

  &__checkbox:checked ~ &__nav {
    opacity: 1;
    width: 100%;
    left: 0;
  }

  // ICON
  &__icon {
    position: relative;
    margin-top: 3.5rem;

    &,
    &::before,
    &::after {
      width: 3rem;
      height: 2px;
      background-color: $color-grey-dark-3;
      display: inline-block;
    }

    &::before,
    &::after {
      content:"";
      position: absolute;
      left: 0;
      transition: all .2s;
    }

    &::before {top: -.8rem;}
    &::after {top: .8rem;}

  }

  &__button:hover &__icon::before {top: -1rem;}
  &__button:hover &__icon::after {top: 1rem;}


  &__checkbox:checked + &__button &__icon { //select sibling and then child
    background-color: transparent; //hide middle dash
  }

  &__checkbox:checked + &__button &__icon::before { //select sibling and then child
    top: 0; //put dash back to middle
    transform: rotate(135deg);
  }

  &__checkbox:checked + &__button &__icon::after { //select sibling and then child
    top: 0;
    transform: rotate(-135deg);
  }
}

