  &--main {
    display: inline-block;
    font-size: 6rem;
    font-weight: 400;
    letter-spacing: 3.5rem; // letter-spacing is applied right side of the chracter
    text-indent: 3.5rem; // so we add text-indent to center the text

    animation-name: moveInLeft;
    animation-duration: 1s;
    animation-timing-function: ease-out;
  }

  &--sub {
    display: inline-block;
    font-size: 2rem;
    font-weight: 700;
    letter-spacing: 1.75rem;
    text-indent: 1.75rem;

    animation-name: moveInRight;
    animation-duration: 1s;
    animation-timing-function: ease-out;
  }
}


.heading-secondary {
  font-size: 3.5rem;
  text-transform: uppercase;
  font-weight: 700;
  display: inline-block;
  background-image: linear-gradient(to right, $color-primary-light, $color-primary-dark);
  -webkit-background-clip: text; // clipping background only that lies under the text
  color: transparent; // if text is transparent we can see the background image
  letter-spacing: .2rem;
  transition: all .2s;
  &:hover {
    transform: skewY(2deg) skewX(15deg) scale(1.1);
    text-shadow: .5rem 1rem 2rem rgba($color-black, .2);
  }
}

.heading-tertiary {
  font-size: $default-font-size;
  font-weight: 700;
  text-transform: uppercase;
}

.paragraph {
  font-size: $default-font-size;
  &:not(:last-child) {
    margin-bottom: 3rem;
  }
}

